  color: #bfc6cb;
  text-align: left;
  vertical-align: middle;
}

div.dokuwiki .pagename a {
  color: #2452c8 !important;
  text-decoration: none !important;
}

div.dokuwiki .logo {
  float: right;
  font-size: 100%;
  font-weight: bolder;
  text-align: right;
  vertical-align: middle;
}

div.dokuwiki .logo a {
  color: #2452c8 !important;
  text-decoration: none !important;
  font-variant: small-caps;
  letter-spacing: 2pt;
}

/* ---------------  top and bottom bar ---------------- */
div.dokuwiki .bar {
/*  border-top: 1px solid #4d5f67;
  border-bottom: 1px solid #4d5f67;*/
  background: #bfc6cb;
/*  padding: 0.1em 0.15em;*/
  clear: both;
}

div.dokuwiki .bar-left {
  float: left;
}

div.dokuwiki .bar-right {
  float: right;
  text-align: right;
}

div.dokuwiki #bar__bottom {
/*  margin-bottom:3px; */
}

/* ------------- File Metadata ----------------------- */

div.dokuwiki div.meta {
  clear: both;
  margin-top: 1em;
  color: #638c9c;
