/**
 * Design elements for default Template
 *
 * @author Andreas Gohr <andi@splitbrain.org>
 * @author Anika Henke <anika@selfthinker.org>
 */

/* -------------- general elements --------------- */

* { padding: 0; margin: 0; }

body {
  font: 100% "Lucida Grande", Verdana, Lucida, Helvetica, Arial, sans-serif;
  background-color: #fff;
  color: #000;
}

/* the document */
div.dokuwiki div.page {
  margin: 4px 2em 0 1em;
  text-align: left;
}

div.dokuwiki table {
  font-size: 100%;
}

div.dokuwiki tr,
div.dokuwiki td,
div.dokuwiki th {
}

div.dokuwiki img {
  border: 0;
}

div.dokuwiki p,
div.dokuwiki blockquote,
div.dokuwiki table,
div.dokuwiki pre {
  margin: 0 0 1.0em 0;
}

div.dokuwiki hr {
  border: 0px;
  border-top: 1px solid #4d5f67;
  text-align: center;
  height: 0px;
}

div.dokuwiki div.nothing {
  text-align: center;
  margin: 2em;
}

/* ---------------- forms ------------------------ */

div.dokuwiki form {
  border: none;
  display: inline;
}

div.dokuwiki label.block {
  display: block;
  text-align: right;
  font-weight: bold;
}

div.dokuwiki label.simple {
  display: block;
  text-align: left;
  font-weight: normal;
}

div.dokuwiki label.block input.edit {
  width: 50%;
}

div.dokuwiki fieldset {
  width: 300px;
  text-align: center;
  border: 1px solid #4d5f67;
  padding: 0.5em;
  margin: auto;
}

div.dokuwiki textarea.edit {
  font-family: monospace;
  font-size: 14px;
  color: #000;
  background-color: #fff;
  border: 1px solid #4d5f67;
  padding: 0.3em 0 0 0.3em;
  width: 100%;
}

/* nice alphatransparency background except for IE <7 */
html>body div.dokuwiki textarea.edit {
  background:  #fff url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/inputshadow.png) repeat-x top;
}

div.dokuwiki input.edit,
div.dokuwiki select.edit {
  font-size: 100%;
  border: 1px solid #4d5f67;
  color: #000;
  background-color: #fff;
  vertical-align: middle;
  margin: 1px;
  padding: 0.20em 0.3em;
  display: inline;
}

/* nice alphatransparency background except for IE <7 */
html>body div.dokuwiki input.edit,
html>body div.dokuwiki select.edit {
  background:  #fff url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/inputshadow.png) repeat-x top;
}

div.dokuwiki select.edit {
  padding: 0.1em 0;
}

div.dokuwiki input.missing {
  font-size: 100%;
  border: 1px solid #4d5f67;
  color: #000;
  background-color: #ffcccc;
  vertical-align: middle;
  margin: 1px;
  padding: 0.20em 0.3em;
  display: inline;
}

/* disabled style - not understood by IE */
div.dokuwiki textarea.edit[disabled],
div.dokuwiki textarea.edit[readonly],
div.dokuwiki input.edit[disabled],
div.dokuwiki input.edit[readonly],
div.dokuwiki select.edit[disabled] {
  background-color: #f5f5f5!important;
  color: #666!important;
}

/* edit form */
div.dokuwiki div.toolbar,
div.dokuwiki div#wiki__editbar {
   margin: 2px 0;
   text-align: left;
}
div.dokuwiki div#size__ctl {
   float: right;
   width: 60px;
   height: 2.7em;
}
div.dokuwiki #size__ctl img {
   cursor: pointer;
}
div.dokuwiki div#wiki__editbar div.editButtons {
   float: left;
   padding: 0 1.0em 0.7em 0;
}
div.dokuwiki div#wiki__editbar div.summary {
   float: left;
}
div.dokuwiki .nowrap {
   white-space: nowrap;
}
div.dokuwiki div#draft__status {
  float: right;
  color: #638c9c;
}

div.dokuwiki form#dw__editform div.license {
  clear: left;
  font-size: 90%;
}

div.dokuwiki p.license {
  font-size: 90%;
  text-align: center;
}

/* --------- buttons ------------------- */

div.dokuwiki input.button,
div.dokuwiki button.button {
  border: 1px solid #4d5f67;
  color: #000;
  background-color: #fff;
  vertical-align: middle;
  text-decoration: none;
  font-size: 100%;
  cursor: pointer;
  margin: 1px;
  padding: 0.125em 0.4em;
}

/* nice alphatransparency background except for IE <7 */
html>body div.dokuwiki input.button,
html>body div.dokuwiki button.button {
  background:  #fff url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/buttonshadow.png) repeat-x bottom;
}

* html div.dokuwiki input.button,
* html div.dokuwiki button.button {
  height: 1.8em;
}

div.dokuwiki div.secedit input.button {
  border: 1px solid #4d5f67;
  color: #000;
  background-color: #fff;
  vertical-align: middle;
  text-decoration: none;
  margin: 0;
  padding: 0;
  font-size: 10px;
  cursor: pointer;
  float: right;
  display: inline;
}

/* ----------- page navigator ------------- */

div.dokuwiki div.pagenav {
    margin: 1em 0 0 0;
}

div.dokuwiki div.pagenav-prev {
    text-align: right;
    float: left;
    width: 49%
}

div.dokuwiki div.pagenav-next {
    text-align: left;
    float: right;
    width: 49%
}

/* --------------- Links ------------------ */

div.dokuwiki a:link,
div.dokuwiki a:visited {
  color: #2452c8;
  text-decoration: none;
}
div.dokuwiki a:hover,
div.dokuwiki a:active {
  color: #000;
  text-decoration: underline;
}

div.dokuwiki h1 a,
div.dokuwiki h2 a,
div.dokuwiki h3 a,
div.dokuwiki h4 a,
div.dokuwiki h5 a,
div.dokuwiki a.nolink {
  color: #000 !important;
  text-decoration: none !important;
}

/* external link */
div.dokuwiki a.urlextern {
  background: transparent url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/link_icon.gif) 0px 1px no-repeat;
  padding: 1px 0px 1px 16px;
  /* font-size:12px; */
  font-size:100%;
  line-height: 16px;
}

div.dokuwiki a.urlextern:link {
  color: #2452c8 !important;
}
div.dokuwiki a.urlextern:visited {
  color: #2452c8 !important;
}
div.dokuwiki a.urlextern:hover {
  color: #000 !important;
}

/* windows share */
div.dokuwiki a.windows {
  background: transparent url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/windows.gif) 0px 1px no-repeat;
  padding: 1px 0px 1px 16px;
}

/* interwiki link (icon are set by dokuwiki) */
div.dokuwiki a.interwiki {
}

/* link to some embedded media */
div.dokuwiki a.media {
}

div.dokuwiki a.urlextern:link,
div.dokuwiki a.windows:link,
div.dokuwiki a.interwiki:link {
  color: #2452c8;
}

div.dokuwiki a.urlextern:visited,
div.dokuwiki a.windows:visited,
div.dokuwiki a.interwiki:visited {
  color: purple;
}
div.dokuwiki a.urlextern:hover,
div.dokuwiki a.urlextern:active,
div.dokuwiki a.windows:hover,
div.dokuwiki a.windows:active,
div.dokuwiki a.interwiki:hover,
div.dokuwiki a.interwiki:active {
  color: #000;
}

/* email link */
div.dokuwiki a.mail {
  background: transparent url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/mail_icon.gif) 0px 1px no-repeat;
  padding: 1px 0px 1px 16px;
}

/* existing wikipage */
div.dokuwiki a.wikilink1 {
  color: #2452c8 !important;
}
div.dokuwiki a.wikilink1:link {
  color: #2452c8 !important;
}
div.dokuwiki a.wikilink1:visited {
  color: #2452c8 !important;
}
div.dokuwiki a.wikilink1:hover {
  color: #000 !important;
}

/* not existing wikipage */
div.dokuwiki a.wikilink2 {
  color: #f30 !important;
  text-decoration: none !important;
  border-bottom: dashed 1px #f30 !important;
}

/* ------------- Page elements ----------------- */

div.dokuwiki div.preview {
  background-color: #f5f5f5;
  margin: 0 0 0 2em;
  padding: 4px;
  border: 1px dashed #000;
}

div.dokuwiki div.breadcrumbs {
  background-color: #f5f5f5;
  color: #666;
  font-size: 80%;
  padding: 0 0 0 4px;
}

div.dokuwiki span.user {
  color: #ccc;
  font-size: 90%;
}

div.dokuwiki li.minor {
  color: #666;
  font-style: italic;
}

/* embedded images */
div.dokuwiki img.media {
  margin: 3px;
}

div.dokuwiki img.medialeft {
  border: 0;
  float: left;
  margin: 0 1.5em 0 0;
}

div.dokuwiki img.mediaright {
  border: 0;
  float: right;
  margin: 0 0 0 1.5em;
}

div.dokuwiki img.mediacenter {
  border: 0;
  display: block;
  margin: 0 auto;
}

/* smileys */
div.dokuwiki img.middle {
  vertical-align: middle;
}

div.dokuwiki acronym {
  cursor: help;
  border-bottom: 1px dotted #000;
}

/* general headline setup */
div.dokuwiki h1,
div.dokuwiki h2,
div.dokuwiki h3,
div.dokuwiki h4,
div.dokuwiki h5 {
    color: #000;
    background-color: inherit;
    font-size: 100%;
    font-weight: normal;
    margin: 0 0 1em 0;
    padding: 0.5em 0 0 0;
    border-bottom: 1px solid #4d5f67;
    clear: left;
}

/* special headlines */
div.dokuwiki h1 {font-size: 160%; margin-left: 0px; font-weight: bold;}
div.dokuwiki h2 {font-size: 150%; margin-left: 20px;}
div.dokuwiki h3 {font-size: 140%; margin-left: 40px; border-bottom: none; font-weight: bold;}
div.dokuwiki h4 {font-size: 120%; margin-left: 60px; border-bottom: none; font-weight: bold;}
div.dokuwiki h5 {font-size: 100%; margin-left: 80px; border-bottom: none; font-weight: bold;}

/* indent different sections */
div.dokuwiki div.level1 {margin-left: 3px;}
div.dokuwiki div.level2 {margin-left: 23px;}
div.dokuwiki div.level3 {margin-left: 43px;}
div.dokuwiki div.level4 {margin-left: 63px;}
div.dokuwiki div.level5 {margin-left: 83px;}

/* unordered lists */
div.dokuwiki ul {
  line-height: 1.5em;
  list-style-type: square;
  list-style-image: none;
  margin: 0 0 1em 3.5em;
  color: #638c9c;
}

/* ordered lists */
div.dokuwiki ol {
  line-height: 1.5em;
  list-style-image: none;
  margin: 0 0 1em 3.5em;
  color: #638c9c;
  font-weight: bold;
}

/* no gap in between nested lists */
div.dokuwiki li ul {
  margin-bottom: 0;
}
div.dokuwiki li ol {
  margin-bottom: 0;
}

/* the list items overriding the ul/ol definition */
div.dokuwiki .li {
  color: #000;
  font-weight: normal;
}

div.dokuwiki ol {list-style-type: decimal}
div.dokuwiki ol ol {list-style-type: upper-roman}
div.dokuwiki ol ol ol {list-style-type: lower-alpha}
div.dokuwiki ol ol ol ol {list-style-type: lower-greek}

div.dokuwiki li.open {
  list-style-image: url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/open.gif);
    /*list-style-type: circle;*/
}

div.dokuwiki li.closed {
  list-style-image: url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/closed.gif);
    /*list-style-type: disc;*/
}

div.dokuwiki blockquote {
  border-left: 2px solid #4d5f67;
  padding-left: 3px;
}

div.dokuwiki pre {
  font-size: 120%;
  padding: 0.5em;
  border: 1px dashed #4d5f67;
  color: #000;
  overflow: auto;
}

/* code blocks by indention */
div.dokuwiki pre.pre {
  background-color: #f7f9fa;
}

/* code blocks by code tag */
div.dokuwiki pre.code {
  background-color: #f7f9fa;
}

/* inline code words */
div.dokuwiki code {
  font-size: 120%;
}

/* code blocks by file tag */
div.dokuwiki pre.file {
  background-color: #bfc6cb;
}

/* inline tables */
div.dokuwiki table.inline {
  background-color: #fff;
  border-spacing: 0px;
  border-collapse: collapse;
}

div.dokuwiki table.inline th {
  padding: 3px;
  border: 1px solid #4d5f67;
  background-color: #bfc6cb;
}

div.dokuwiki table.inline td {
  padding: 3px;
  border: 1px solid #4d5f67;
}

/* ---------- table of contents ------------------- */

div.dokuwiki div.toc {
  margin: 1.2em 0 0 2em;
  float: right;
  width: 200px;
  font-size: 80%;
  clear: both;
}

div.dokuwiki div.tocheader {
  border: 1px solid #4d5f67;
  background-color: #bfc6cb;
  text-align: left;
  font-weight: bold;
  padding: 3px;
  margin-bottom: 2px;
}

div.dokuwiki span.toc_open,
div.dokuwiki span.toc_close {
    border: 0.4em solid #bfc6cb;
    float: right;
    display: block;
    margin: 0.4em 3px 0 0;
}

div.dokuwiki span.toc_open span,
div.dokuwiki span.toc_close span {
    display: none;
}

div.dokuwiki span.toc_open {
    margin-top: 0.4em;
    border-top: 0.4em solid #000;
}

div.dokuwiki span.toc_close {
    margin-top: 0;
    border-bottom: 0.4em solid #000;
}

div.dokuwiki #toc__inside {
  border: 1px solid #4d5f67;
  background-color: #fff;
  text-align: left;
  padding: 0.5em 0 0.7em 0;
}

div.dokuwiki ul.toc {
  list-style-type: none;
  list-style-image: none;
  line-height: 1.2em;
  padding-left: 1em;
  margin: 0;
}

div.dokuwiki ul.toc li {
  background: transparent url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/tocdot2.gif) 0 0.6em no-repeat;
  padding-left: 0.4em;
}

div.dokuwiki ul.toc li.clear {
  background-image: none;
  padding-left: 0.4em;
}

div.dokuwiki a.toc:link,
div.dokuwiki a.toc:visited {
  color: #2452c8;
}

div.dokuwiki a.toc:hover,
div.dokuwiki a.toc:active {
  color: #000;
}

/* ---------------------------- Diff rendering --------------------------*/
div.dokuwiki table.diff {
  background-color: #fff;
  width: 100%;
}
div.dokuwiki td.diff-blockheader {
  font-weight: bold;
}
div.dokuwiki table.diff th {
  border-bottom: 1px solid #4d5f67;
  font-size: 120%;
  width: 50%;
  font-weight: normal;
  text-align: left;
}
div.dokuwiki table.diff td {
  font-family: monospace;
  font-size: 100%;
}
div.dokuwiki td.diff-addedline {
  background-color: #ddffdd;
}
div.dokuwiki td.diff-deletedline {
    background-color: #ffffbb;
}
div.dokuwiki td.diff-context {
    background-color: #f5f5f5;
}
div.dokuwiki table.diff td.diff-addedline strong,
div.dokuwiki table.diff td.diff-deletedline strong {
    color: red;
}

/* --------------------- footnotes -------------------------------- */

div.dokuwiki div.footnotes {
  clear: both;
  border-top: 1px solid #4d5f67;
  padding-left: 1em;
  margin-top: 1em;
}

div.dokuwiki div.fn {
  font-size: 90%;
}

div.dokuwiki a.fn_bot {
  font-weight: bold;
}

/* insitu-footnotes */
div.insitu-footnote {
  font-size: 80%;
  line-height: 1.2em;
  border: 1px solid #4d5f67;
  background-color: #f7f9fa;
  text-align: left;
  padding: 4px;
  max-width: 40%;    /* IE's width is handled in javascript */
}

/* overcome IE issue with one line code or file boxes which require h. scrolling */
* html .insitu-footnote pre.code,
* html .insitu-footnote pre.file {
  padding-bottom: 18px;
}

/* --------------- search result formating --------------- */
div.dokuwiki .search_result {
  margin-bottom: 6px;
  padding: 0 10px 0 30px;
}

div.dokuwiki .search_snippet {
  color: #ccc;
  font-size: 12px;
  margin-left: 20px;
}

div.dokuwiki .search_sep {
  color: #000;
}

div.dokuwiki .search_hit {
  color: #000;
  background-color: #ff9;
}
div.dokuwiki strong.search_hit {
  font-weight: normal;
}

div.dokuwiki div.search_quickresult {
  margin: 0 0 15px 30px;
  padding: 0 10px 10px 0;
  border-bottom: 1px dashed #4d5f67;
}
div.dokuwiki div.search_quickresult h3 {
  margin: 0 0 1.0em 0;
  font-size: 1em;
  font-weight: bold;
}

div.dokuwiki ul.search_quickhits {
  margin: 0 0 0.5em 1.0em;
}

div.dokuwiki ul.search_quickhits li {
  margin: 0 1.0em 0 1.0em;
  float:left;
  width: 30%;
}

/* ------------------ Additional ---------------------- */

div.footerinc {
    text-align: center;
}
.footerinc a img {
    opacity: 0.5;
    border: 0;
}

.footerinc a:hover img {
    opacity: 1;
}

/* ---------- AJAX quicksearch ----------- */

div.dokuwiki div.ajax_qsearch {
  position: absolute;
  right: 237px;;
  width: 200px;
  opacity: 0.9;
  display: none;
  font-size: 80%;
  line-height: 1.2em;
  border: 1px solid #4d5f67;
  background-color: #f7f9fa;
  text-align: left;
  padding: 4px;
}

/* --------- Toolbar -------------------- */
button.toolbutton {
  background-color: #fff;
  padding: 0px;
  margin: 0 1px 0 0;
  border: 1px solid #4d5f67;
  cursor: pointer;
}

/* nice alphatransparency background except for IE <7 */
html>body button.toolbutton {
  background:  #fff url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/buttonshadow.png) repeat-x bottom;
}

div.picker {
  width: 250px;
  border: 1px solid #4d5f67;
  background-color: #bfc6cb;
}

button.pickerbutton {
  padding: 0px;
  margin: 0 1px 1px 0;
  border: 0;
  background-color: transparent;
  font-size: 80%;
  cursor: pointer;
}

/* ---------------  Image Details  ----------------- */

div.dokuwiki div.img_big {
  float: left;
  margin-right: 0.5em;
}

div.dokuwiki dl.img_tags dt {
  font-weight: bold;
  background-color: #bfc6cb;
}
div.dokuwiki dl.img_tags dd {
  background-color: #f5f5f5;
}

div.dokuwiki div.imagemeta {
  color: #666;
  font-size: 70%;
  line-height: 95%;
}

div.dokuwiki div.imagemeta img.thumb {
  float:left;
  margin-right: 0.1em;
}
/**
 * The CSS in here cotrols the appearance of the media manager
 */

#media__manager {
    height: 100%;
    overflow: hidden;
}

#media__left {
    width: 30%;
    border-right: solid 1px #4d5f67;

    height: 100%;
    overflow: auto;
    position: absolute;
    left: 0;
}

#media__right {
    width: 69.7%;

    height: 100%;
    overflow: auto;
    position: absolute;
    right: 0;
}

#media__manager h1 {
    margin: 0;
    padding: 0;
    margin-bottom: 0.5em;
}

/* --- Tree formatting --- */

#media__tree img {
    float:left;
    padding: 0.5em 0.3em 0 0;
}

#media__tree ul {
    list-style-type: none;
    list-style-image: none;
    margin-left: 1.5em;
}

#media__tree li {
    clear: left;
    list-style-type: none;
    list-style-image: none;
}
*+html #media__tree li,
* html #media__tree li {
    border: 1px solid #fff;
}/* I don't understand this, but this fixes a style bug in IE;
it's dirty, so any "real" fixes are welcome */

/* --- options --- */

#media__opts {
    padding-left: 1em;
    margin-bottom: 0.5em;
}

#media__opts input {
    float: left;
    display: block;
    margin-top: 4px;
    position: absolute;
}
*+html #media__opts input,
* html #media__opts input {
    position: static;
}

#media__opts label {
    display: block;
    float: left;
    margin-left: 20px;
    margin-bottom: 4px;
}
*+html #media__opts label,
* html #media__opts label {
    margin-left: 10px;
}

#media__opts br {
    clear: left;
}

/* --- file list --- */

#media__content img.load {
    margin: 1em auto;
}

#media__content #scroll__here {
    border: 1px dashed #4d5f67;
}

#media__content .odd {
    background-color: #f7f9fa;
    padding: 0.4em;
}

#media__content .even {
    padding: 0.4em;
}

#media__content a.mediafile {
    margin-right: 1.5em;
    font-weight: bold;
}

#media__content div.detail {
    padding: 0.3em 0 0.3em 2em;
}

#media__content div.detail div.thumb {
    float: left;
    width: 130px;
    text-align: center;
    margin-right: 0.4em;
}


#media__content img.btn {
    vertical-align: text-bottom;
}

#media__content div.example {
    color: #666;
    margin-left: 1em;
}

/* --- upload form --- */

#media__content div.upload {
    font-size: 90%;
    padding: 0 0.5em 0.5em 0.5em;
}

#media__content form#dw__upload,
#media__content div#dw__flashupload {
    display: block;
    border-bottom: solid 1px #4d5f67;
    padding: 0 0.5em 1em 0.5em;
}
#media__content form#dw__upload fieldset {
    padding: 0;
    margin: 0;
    border: none;
    width: auto;
}
#media__content form#dw__upload p {
    text-align: left;
    padding: 0.25em 0;
    margin: 0;
    line-height: 1.0em;
}
#media__content form#dw__upload label.check {
    float: none;
    width: auto;
    margin-left: 11.5em;
}

/* --- meta edit form --- */

#media__content form.meta {
    display: block;
    padding: 0 0 1em 0;
}

#media__content form.meta label {
    display: block;
    width: 25%;
    float: left;
    font-weight: bold;
    margin-left: 1em;
    clear: left;
}

#media__content form.meta .edit {
    font: 100% "Lucida Grande", Verdana, Lucida, Helvetica, Arial, sans-serif;
    float: left;
    width: 70%;
    padding-right: 0;
    padding-left: 0.2em;
    margin: 2px;
}

#media__content form.meta textarea.edit {
    height: 8em;
}

#media__content form.meta div.metafield {
    clear: left;
}

#media__content form.meta div.buttons {
    clear: left;
    margin-left: 20%;
    padding-left: 1em;
}
/**
 * Extra styles for sidebar template
 *
 * @author Christopher Smith <chris@jalakai.co.uk>
 */

/* dokuwiki containers & styles */

/* sidebar orientation and position */
#sidebar {
  width:23%; /* 20%; */ 
  margin:0; 
  padding:0;
  position: relative;
}

#sidebartop {
  position: absolute;
  top: -1.2em;
  width: 100%;
  height: 1.2em;
  background: #f5f5f5;
}

.sidebar_inside_left #sidebar {
    float:left; 
}

.sidebar_inside_right #sidebar {
    float:right; 
}

.sidebar_inside_left .dokuwiki div.breadcrumbs {
    float: right;
    width: 75%; /*78%;*/
    padding: 0 1% 0 0.9%;
}

.sidebar_inside_left .dokuwiki .page, .sidebar_inside_left .dokuwiki .meta {
    float:right; 
    width: 74%; /*77%;*/             /* also see IE Win fix below */
    margin-right: 1%;
    margin-left:0;
}

.sidebar_inside_right .dokuwiki .page, .sidebar_inside_right .dokuwiki .meta, .sidebar_inside_right .dokuwiki div.breadcrumbs {
    float:left; 
    width: 74%; /*77%;*/ 
    margin-left: 1%;
    margin-right: 0;
}

.sidebar_outside_left #sidebar {
    position:absolute; 
    top:0; 
    left:0;
}

.sidebar_outside_right #sidebar {
    position:absolute; 
    top:0; 
    right:0;
}

.sidebar_outside_left .dokuwiki {
    padding-left: 24%; /*21%;*/
}

.sidebar_outside_right .dokuwiki {
    padding-right: 24%; /*21%;*/
}

.sidebar_outside_left .footerinc {
    padding-left: 24%; /*21%;*/
}

.sidebar_outside_right .footerinc {
    padding-right: 24%; /*21%;*/
}

/* sidebar presentation */
/* the following three styles use a faux-column image to place a separating line 
   between the sidebar and dokuwiku */
.sidebar_outside_left .dokuwiki, .sidebar_inside_left .dokuwiki {
    background: url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/sidebar-border.gif) repeat-y 23%; /*20%;*/
}

.sidebar_outside_right .dokuwiki, .sidebar_inside_right .dokuwiki {
    background: url(https://cw.felk.cvut.cz/lib/tpl/cmp/images/sidebar-border.gif) repeat-y 77%; /*80%;*/
}

/* hide the line where it passes through .stylehead */
.stylehead {
    background: #fff;
}

/* sidebar contents */
#sidebar {
/*    font-size:10px; */
    font-size:12px; 
/*      font-size 70%;*/
}
#sidebar a {
    color: #2452c8;
}

#sidebar a.wikilink2 {
    color: #666;
}

#sidebar a.wikilink2:hover {
    text-decoration:none; 
    cursor:default;
}

#sidebar h1 {
    font-size:140%; 
    margin-left: 0px; 
    padding-left: 2px; 
    font-weight:bold; 
    padding-bottom:0; 
    background-color: #bfc6cb;
}
#sidebar h2 {
    font-size:120%; 
    margin-left: 4px; 
    font-weight:bold; 
    padding-bottom:0;
}
#sidebar h3 {
    font-size:120%; 
    margin-left: 8px; 
    font-weight:normal; 
    padding-bottom:0;
}
#sidebar h4 {
    font-size:100%; 
    margin-left: 12px; 
    font-weight:bold; 
    padding-bottom:0;
}
#sidebar h5 {
    font-size:100%; 
    margin-left: 16px; 
    font-weight:normal; 
    padding-bottom:0;
}
#sidebar .toc {
    display:none;
}

#sidebar .secedit {
}

/* reduced section indentation */
#sidebar div.level1 {margin-left: 2px;}
#sidebar div.level2 {margin-left: 6px;}
#sidebar div.level3 {margin-left: 10px;}
#sidebar div.level4 {margin-left: 14px;}
#sidebar div.level5 {margin-left: 18px;}

/* IE fixes (hide from IE Mac) \*/

* html .page .toc {height:1px}    /* General Dokuwiki fix. IE needs this to always display TOC contents \*/
* html pre {width:95%;}           /* General Dokuwiki fix - very important for Sidebar. IE needs this to get the overflow:auto style to kick in \*/
* html .stylehead {height:1px;}   /* Get IE in hasLayout mode to ensure the background covers the whole element \*/

* html .sidebar_inside_left .page, * html .sidebar_inside_right .page, 
* html .sidebar_inside_left .meta, * html .sidebar_inside_right .meta {
    width: 74%; /*77%;*/           /* IE needs extra gap to ensure #sidebar & .page float next to each other \*/
    overflow-x: auto;             /* IE proprietary property to prevent wide images in wiki page forcing sidebar down below wiki page \*/
                                  /* 'overflow-x:auto;' maybe replaced by 'overflow:auto;' to ensure template passes w3c validation \*/
}

/* (end IE Mac hiding) */

/* duplicate standard DW styles with increased specificity to counter some sidebar styles */
.sidebar_outside_left .insitu-footnote, .sidebar_inside_left .insitu-footnote {
  background-color: #f7f9fa;
}
